import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Container,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Typography,
  Snackbar,
  Alert,
  Box,
} from "@mui/material";

function App() {
  const [records, setRecords] = useState([]);
  const [newRecord, setNewRecord] = useState({ id: "", name: "", email: "" });
  const [editRecord, setEditRecord] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState("");
  const [csvUploaded, setCsvUploaded] = useState(false);

  useEffect(() => {
    fetchRecords();
  }, []);

  const fetchRecords = async () => {
    try {
      const response = await axios.get("http://localhost:8080/api/records");
      if (response.data && response.data.length > 0) {
        setRecords(response.data);
        setCsvUploaded(true);
      } else {
        setRecords([]);
        setCsvUploaded(false);
      }
    } catch (error) {
      console.error("Error fetching records:", error);
    }
  };

  const addRecord = async () => {
    try {
      await axios.post("http://localhost:8080/api/records", newRecord);
      fetchRecords();
      setNewRecord({ id: "", name: "", email: "" });
      showSnackbar("Record added successfully!");
    } catch (error) {
      console.error("Error adding record:", error);
    }
  };

  const deleteRecord = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/records/${id}`);
      fetchRecords();
      showSnackbar("Record deleted successfully!");
    } catch (error) {
      console.error("Error deleting record:", error);
    }
  };

  const handleUpdateRecord = async () => {
    try {
      await axios.put(`http://localhost:8080/api/records/${editRecord.id}`, editRecord);
      fetchRecords();
      setOpenDialog(false);
      showSnackbar("Record updated successfully!");
    } catch (error) {
      console.error("Error updating record:", error);
    }
  };

  const handleUploadCSV = async (event) => {
    const formData = new FormData();
    formData.append("file", event.target.files[0]);

    try {
      await axios.post("http://localhost:8080/api/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      fetchRecords();
      showSnackbar("CSV uploaded successfully!");
    } catch (error) {
      console.error("Error uploading CSV:", error);
    }
  };

  const handleRemoveCSV = async () => {
    try {
      await axios.delete("http://localhost:8080/api/remove");
      setCsvUploaded(false);
      setRecords([]);
      showSnackbar("CSV file removed successfully!");
    } catch (error) {
      console.error("Error removing CSV file:", error);
    }
  };

  const showSnackbar = (message) => {
    setSnackbarMessage(message);
    setOpenSnackbar(true);
  };

  return (
    <Container>
      <Typography variant="h3" gutterBottom align="center">
        CSV CRUD App
      </Typography>

      {!csvUploaded ? (
        <Box display="flex" flexDirection="column" alignItems="center" mt={4}>
          <Typography variant="h5" gutterBottom>
            No Data Available
          </Typography>
          <Typography variant="body1" gutterBottom>
            Please upload a CSV file to view and manage records.
          </Typography>
          <Button variant="contained" color="secondary" component="label">
            Upload CSV
            <input type="file" hidden onChange={handleUploadCSV} />
          </Button>
        </Box>
      ) : (
        <>
          <Box mb={3}>
            <TextField
              label="ID"
              value={newRecord.id}
              onChange={(e) => setNewRecord({ ...newRecord, id: e.target.value })}
              style={{ marginRight: "10px" }}
            />
            <TextField
              label="Name"
              value={newRecord.name}
              onChange={(e) => setNewRecord({ ...newRecord, name: e.target.value })}
              style={{ marginRight: "10px" }}
            />
            <TextField
              label="Email"
              value={newRecord.email}
              onChange={(e) => setNewRecord({ ...newRecord, email: e.target.value })}
              style={{ marginRight: "10px" }}
            />
            <Button onClick={addRecord} variant="contained" color="primary">
              Add Record
            </Button>
          </Box>

          <Table>
            <TableHead>
              <TableRow>
                <TableCell>ID</TableCell>
                <TableCell>Name</TableCell>
                <TableCell>Email</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {records.map((record) => (
                <TableRow key={record.id}>
                  <TableCell>{record.id}</TableCell>
                  <TableCell>{record.name}</TableCell>
                  <TableCell>{record.email}</TableCell>
                  <TableCell>
                    <Button
                      onClick={() => {
                        setEditRecord(record);
                        setOpenDialog(true);
                      }}
                      variant="outlined"
                      color="primary"
                      style={{ marginRight: "10px" }}
                    >
                      Edit
                    </Button>
                    <Button
                      onClick={() => deleteRecord(record.id)}
                      variant="outlined"
                      color="secondary"
                    >
                      Delete
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>

          <Box mt={2}>
            <Button onClick={handleRemoveCSV} variant="contained" color="error">
              Remove CSV File
            </Button>
          </Box>
        </>
      )}

      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>Edit Record</DialogTitle>
        <DialogContent>
          <TextField
            label="ID"
            value={editRecord?.id || ""}
            onChange={(e) => setEditRecord({ ...editRecord, id: e.target.value })}
            fullWidth
            style={{ marginBottom: "10px" }}
          />
          <TextField
            label="Name"
            value={editRecord?.name || ""}
            onChange={(e) => setEditRecord({ ...editRecord, name: e.target.value })}
            fullWidth
            style={{ marginBottom: "10px" }}
          />
          <TextField
            label="Email"
            value={editRecord?.email || ""}
            onChange={(e) => setEditRecord({ ...editRecord, email: e.target.value })}
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)} color="primary">
            Cancel
          </Button>
          <Button onClick={handleUpdateRecord} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar open={openSnackbar} autoHideDuration={6000} onClose={() => setOpenSnackbar(false)}>
        <Alert severity="success" onClose={() => setOpenSnackbar(false)}>
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default App;
